mapper.select:
SELECT c.* FROM course c


        c.rank = #{rank} and

    c.is_free = #{isFree}

SELECT c.* FROM course c


        c.rank = #{rank} and

    c.ct_id = #{cid} and c.is_free = #{isFree}

SELECT c.* FROM course c LEFT JOIN course_type ct on c.ct_id = ct.id
c.rank = #{rank} and
ct.parent_id = #{pid} and c.is_free = #{isFree}
